def readCSVDict(filename):
    rows = csv.DictReader(open(filename))
    return rows   

def writeCSVDict(filename, dataset):
    with open(filename, 'wb') as f:
        w = csv.DictWriter(f, dataset[0].keys())
        w.writeheader()
        w.writerows(dataset)

